mapper.select:
select count(*) from employee
where employee_name=#{employeeName}

  AND id != #{id}

SELECT employee.id,employee_name,use_state,employee_code,employee.role_id, employee_tel,is_operator,sys_role.rolename FROM employee LEFT JOIN sys_role ON employee.role_id=sys_role.id
AND employee_name like CONCAT('%',#{employeeName},'%')
AND employee_code LIKE CONCAT('%',#{employeeCode},'%')
AND role_id = #{roleId}
AND use_state = #{useState}
ORDER BY create_time DESC

mapper.update:
UPDATE employee
SET use_state=#{useState},
    mod_time=#{modTime}
WHERE id=#{id}

UPDATE employee
SET employee_password='28c8edde3d61a0411511d3b1866f0636',
    mod_time=#{modTime}
WHERE id=#{id}

mapper.select:
 SELECT
 employee.id,employee_name,use_state,employee_code,employee.role_id, employee_tel,is_operator
 FROM employee
where (employee_name= #{employeeName,jdbcType=VARCHAR} or employee_code=#{employeeName,jdbcType=VARCHAR}) 
and is_operator=#{isOperator,jdbcType=INTEGER}
and use_state=#{useState,jdbcType=INTEGER}

mapper.update:
UPDATE employee
SET employee_password=#{newEmployeePassword},
    mod_time=#{modTime}
WHERE id = #{id} and employee_password = #{employeePassword}

mapper.select:
select LTRIM(sys_function.code) as functionCode, sys_function.name as functionName
from sys_role_function,sys_function
where sys_role_function.function_id=sys_function.id and role_id = #{roleId,jdbcType=INTEGER}
 and sys_function.use_state=0

    and sys_function.code = #{functionCode,jdbcType=VARCHAR}
