mapper.select:
SELECT mt.id, m.medicine_name AS parent_medicine_name, md.medicine_name AS sub_medicine_name, mt.create_time, mt.mod_user, mt.mod_time,taboo_type,mt.sub_medicine_id,mt.parent_medicine_id, sl.label AS taboo_type_name FROM medicine_taboo mt LEFT JOIN medicine m ON mt.parent_medicine_id=m.id LEFT JOIN medicine md ON mt.sub_medicine_id=md.id LEFT JOIN sys_lookup sl ON sl.value=mt.taboo_type
sl.code='tabooType'
AND ( m.medicine_name LIKE CONCAT('%',#{parentMedicineName},'%') OR m.initial_code LIKE CONCAT('%',#{parentMedicineName},'%') OR m.mnemonic_code LIKE CONCAT('%',#{parentMedicineName},'%') OR md.medicine_name LIKE CONCAT('%',#{parentMedicineName},'%') OR md.initial_code LIKE CONCAT('%',#{parentMedicineName},'%') OR md.mnemonic_code LIKE CONCAT('%',#{parentMedicineName},'%') )
AND mt.taboo_type=#{tabooType}
AND mt.id=#{id}
ORDER BY mt.create_time DESC
