mapper.delete:
DELETE FROM special_discount

  settle_company_id = #{settleCompanyId}

    AND medicine_id = #{medicineId,jdbcType=INTEGER}

mapper.select:
    select medicine_id,IFNULL(discount,0) as discount,IFNULL(tax_rate,0) as tax_rate,
    settle_company_id from special_discount


	      settle_company_id= #{settleCompanyId}
